import React, { useState } from 'react';
import './App.css';

function HomePage() {
  const [selectedPost, setSelectedPost] = useState(null);

  const posts = [
    { id: 1, title: 'React 프로젝트 신규 시작!', author: '김철수', content: '새로운 React 프로젝트를 시작합니다. 많은 참여 부탁드립니다.' },
    { id: 2, title: 'Node.js 서버 업데이트', author: '박민수', content: 'Node.js 서버를 최신 버전으로 업데이트했습니다. 버그 수정 및 성능 향상이 포함됩니다.' },
    { id: 3, title: '배포 자동화 설정', author: '이영희', content: '배포 자동화 파이프라인을 설정했습니다. 이제 CI/CD 파이프라인을 통해 자동 배포가 가능합니다.' }
  ];

  const handlePostClick = (postId) => {
    const post = posts.find(p => p.id === postId);
    setSelectedPost(post);
  };

  return (
    <div className="App">
      <header>
        <h1>개발팀</h1>
        <nav>
          <a href="#announcements">공지사항</a>
          <a href="#posts">게시글</a>
        </nav>
      </header>

      <section id="announcements">
        <h2>공지사항</h2>
        <ul>
          <li>2024년 12월 1일: 팀 프로젝트 발표회</li>
          <li>2024년 12월 15일: 코드 리뷰 세션</li>
        </ul>
      </section>

      <section id="posts">
        <h2>게시글</h2>
        <ul>
          {posts.map(post => (
            <li key={post.id} onClick={() => handlePostClick(post.id)}>
              <h3>{post.title}</h3>
              <p>작성자: {post.author}</p>
            </li>
          ))}
        </ul>

        {selectedPost && (
          <div className="post-detail">
            <h3>{selectedPost.title}</h3>
            <p><strong>작성자:</strong> {selectedPost.author}</p>
            <p>{selectedPost.content}</p>
            <button onClick={() => setSelectedPost(null)}>닫기</button>
          </div>
        )}
      </section>

      <footer>
        <p>&copy; 2024 개발팀 | All Rights Reserved</p>
      </footer>
    </div>
  );
}

export default HomePage;
